        /**
         * method to uninstall the component
         *
         * @return void
         */
        function uninstall($parent) 
        {
	    JLoader::register('JControllerLegacy', JPATH_LIBRARIES.'legacy/controller/legacy.php');
	    $session = JFactory::getSession();
	    if($session->get('droptablesUninstall', false)==false){
		$session->set('droptablesUninstall', true);
		JFactory::getApplication()->enqueueMessage(JText::_('COM_DROPTABLES_INSTALLER_UNINSTALL_DB'), 'warning');
		$controller = new JControllerLegacy();
		$controller->setRedirect('index.php?option=com_installer&view=manage&confirm=1');
		$controller->redirect();
	    }
	    
	    $dbo = JFactory::getDbo();
            $queries = array();
	    $queries[] = "DROP TABLE IF EXISTS `#__droptables_tables`";
	    $queries[] = "DROP TABLE IF EXISTS `#__droptables_styles`";
		$queries[] = "DROP TABLE IF EXISTS `#__droptables_charts`";
		$queries[] = "DROP TABLE IF EXISTS `#__droptables_charttyles`";
		
            foreach ($queries as $query){
                $dbo->setQuery($query);
                $dbo->query();
            }
        }
 
