        /**
         * method to run before an install/update/uninstall method
         *
         * @return void
         */
        function preflight($type, $parent) 
        {
            if($type=='uninstall'){

            }elseif($type=='update'){
                $this->oldRelease = $this->getVersion('com_droptables');
                if(version_compare($this->oldRelease, $parent->get( 'manifest' )->version,'gt')){
                    Jerror::raiseWarning(null, 'You already have a newer version of Droptables');
                    jimport('joomla.application.component.controler');
                    $controller = new JController();
                    $controller->setRedirect('index.php?option=com_installer&view=install');
                    $controller->redirect();
                    return false;
                }
            }
            else{
                // $parent is the class calling this method
                // $type is the type of change (install, update or discover_install)
                $this->release = $parent->get( 'manifest' )->version;
                $jversion = new JVersion();
                // abort if the current Joomla release is older
                if( version_compare( $jversion->getShortVersion(), '3.3.0', 'lt' ) ) {
                        Jerror::raiseWarning(null, 'Cannot install Droptables component in a Joomla release prior to 3.3.0');
                        $controller = new JController();
                        $controller->setRedirect('index.php?option=com_installer&view=install');
                        $controller->redirect();
                        return false;
                }
            }
	    $session = JFactory::getSession();
	    $session->set('droptablesUninstall', false);
        }
 
